import './App.css';
import { Route, Switch } from 'react-router-dom';
import Header from './component/Header';
import Index from './question/Index';
import Show from './question/Show';
import New from './user/New';
import Login from './user/Login';
import useToken from './auth/useToken';
import Auth from './auth/Auth';
import TestAuth from './auth/testAuth';
import {useState} from 'react';

function App() {
  const {token, setToken} = useToken();
  const [test, setTest1]   = useState("");



  return (
    <div>
      <TestAuth value={ test } />
      <Header token={token} />
        <Switch>
          <Route exact path="/signup" component={ New } />
          <Route exact path="/login" component={ Login } />
        </Switch>
        <Auth token={token} >
          <Switch>
            <Route exact path="/" component={ Index } />
            <Route exact path="/question/:id" component={ Show } />
          </Switch>
        </Auth>
    </div>
  );
}

export default App;
