import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {Link as L} from 'react-router-dom';

const Index = (props) => {
    const [questions, setQuestions] = useState();
    const [answer, setAnswer] = useState([]);

    let as = [];
    axios.get("http://localhost:8001/questions")
    .then((res) => {
        console.log(res.data);
        res.data.qs.map(question => (
          axios.get(`http://localhost:8001/answers/${question.id}`)
          .then((results) => {
              as.push(results.data.answer);
              setAnswer(as);
              console.log(answer);
          })
        ))
        setQuestions(res.data.qs);
    })
    .catch((errors) => {
        console.log(errors);
    })


    return(
      <div className="container">
      <div className="row">
      {questions === null && <p>Loading questions...</p>}
          {
            questions && questions.map(question => (
              <div key={question.id} className="col-sm-12 col-md-4 col-lg-3">
                <L to={`/question/${question.id}`}>
                  <div className="card text-white bg-success mb-3">
                    <div className="card-header">Answers: {
                      answer && answer.map(ans => (
                        question.id === ans.id ? <p key={ans.id}>{ans.content}</p> : ""
                      ))
                    }</div>
                    <div className="card-body">
                      <h4 className="card-title">{question.title}</h4>
                      <p className="card-text">{question.description}</p>
                    </div>
                  </div>
                </L>
              </div>
            ))
          }
      </div>
    </div>
    )
}

export default Index;
