class AuthUser{

  isLoggedIn = () => this.get('token') === 'true';

  get = key => this.getLocalStorage(key);

  set = (key, value) => localStorage.setItem(key, JSON.stringify(value));

  getLocalStorage = key => {
    const ret = localStorage.getItem(key);
    if (ret) {
      return ret;
    }
    return null;
  };

}

export default new AuthUser();
