import React, { useState } from 'react';
import axios from 'axios';

const New = (porps) => {

    const [myname, setName] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [password_confirmation, setPasswordConfirmation] = useState("");

    const handleInput = event => {
        // eslint-disable-next-line default-case
        switch (event.target.name){
          case 'myname':
            setName(event.target.value);
            break;
          case 'email':
            setEmail(event.target.value);
            break;
          case 'password':
            setPassword(event.target.password);
            break;
          case 'password_confirmation':
            setPasswordConfirmation(event.target.password_confirmation);
            break;
        }
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        console.log(myname);
    }

    return(
        <div className="container">
        <div className="row">
            <div className="col-12">
                <div className="card border-primary">
                    <div className="card-header"> Sign Up </div>
                    <div className="card-body text-left">
                        <form onSubmit={handleSubmit} >
                        <div className="form-group">
                          <label htmlFor="exampleInputEmail1">Name:</label>
                          <input
                            type="text"
                            name="myname"
                            value={(e) => {this.updateTitle(e.target.value)}}
                            className="form-control"
                            placeholder="Give your question a title."
                          />
                        </div>
                        <div className="form-group">
                          <label htmlFor="exampleInputEmail1">Email:</label>
                          <input
                            type="text"
                            name="email"
                            onBlur={(e) => {this.updateDescription(e.target.value)}}
                            className="form-control"
                            placeholder="Give more context to your question."
                          />
                        </div>
                        <div className="form-group">
                          <label htmlFor="exampleInputEmail1">Password:</label>
                          <input
                            type="text"
                            name="password"
                            onBlur={(e) => {this.updateDescription(e.target.value)}}
                            className="form-control"
                            placeholder="Give more context to your question."
                          />
                        </div>
                        <div className="form-group">
                          <label htmlFor="exampleInputEmail1">Password_confirmation:</label>
                          <input
                            type="text"
                            name="password"
                            onBlur={(e) => {this.updateDescription(e.target.value)}}
                            className="form-control"
                            placeholder="Give more context to your question."
                          />
                        </div>
                        <button className="btn btn-primary" >Submit</button>
                        </form>
                    </div>
                </div>

            </div>

        </div>
    </div>
    )
}

export default New;
