import {useState} from 'react'

const useTestState = (localitem) => {
    const [loc, setState] = useState(localStorage.getItem(localitem));

    const setLoc = (newitem) => {
        localStorage.setItem(localitem, newitem);
        setState(newitem);
    }

    return [loc, setLoc]
}

export default useTestState;
